import Vue from 'vue';
import Vuex from 'vuex';
import createPersistedState from 'vuex-persistedstate';
import cvtgate from "./modules/cvtgate";
import farm from "./modules/farm";
import field from "./modules/field";
import observation from "./modules/observation";
import device from "./modules/device";
import mqtt from "./modules/mqtt";
import user from "./modules/user";
import dataIndex from "./modules/dataIndex";
Vue.use(Vuex);
var store = new Vuex.Store({
  modules: {
    cvtgate: cvtgate,
    farm: farm,
    field: field,
    observation: observation,
    device: device,
    mqtt: mqtt,
    user: user,
    dataIndex: dataIndex
  },
  plugins: [createPersistedState({
    paths: ['user']
  })]
});
store.watch(function (state, getters) {
  return getters['field/getFields'];
}, function (val) {
  store.dispatch('device/fetchDataDevice');
});
export default store;
