result:
.suntime {
  fill: none;
  stroke-width: 10px;
  stroke-opacity: 0.7;
  stroke-dasharray: 2, 50;
  transition: all 1s;
}

.line {
  fill: none;
  stroke-width: 2px;
  stroke-opacity: 0.4;
  transition: all 1s;
  shape-rendering: optimizeQuality;
}

.section {
  fill: none;
  stroke-width: 1.5px;
  stroke-opacity: 0.5;
  stroke-dasharray: 2.5, 2.5;
  transition: all 1s;
}

.dash {
  fill: none;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  /* stroke-dasharray: 3px 3px; */
  stroke-width: 4px;
  stroke-opacity: 0.6;
  transition: all 1s;
  stroke-linejoin: round;
  stroke-linecap: round;
}

.dash-ani {
  fill: none;
  stroke-dasharray: 3px 3px;
  stroke-linejoin: round;
  stroke-linecap: round;
  -webkit-animation: moving-dashes 1s linear infinite;
}
