export default {
  props: {
    xScale: {
      required: false,
      type: Function,
      default: Function
    },
    yScale: {
      required: false,
      type: Function,
      default: Function
    },
    parameters: {
      required: false,
      type: Object,
      default: Object
    },
    xScaleFunction: {
      type: Function,
      default: function name (params) {
        return params
      }
    }
  },
  methods: {
    classObject (item) {
      if (item.bulletStyle === undefined) {
        return 'dot'
      } else {
        return item.bulletStyle
      }
    },
    x (x) {
      return this.xScale(this.xScaleFunction(x))
    },
    y (y) {
      return this.yScale(y)
    }
  }
}
